import { ModuleProfile, Api, PluginProfile } from '../types';
declare type Profile<T extends Api> = ModuleProfile<T> | PluginProfile<T>;
/**
 * Create a Profile with default values
 * @param profile The profile of the plugin
 * @param baseProfiles A list of profiles to mix in the base profile
 */
export declare function extendsProfile<T extends Api, U extends Api>(profile: Profile<T>, ...baseProfiles: ModuleProfile<U>[]): Profile<T & U>;
export {};
